CREATE FUNCTION public."SP_ADMIN_DASHBOARD_LOW_STOCK_PRODUCTS_GET"() RETURNS TABLE("Product_ID" integer, "Product_Name" character varying, "Category_Name" text, "Quantity" integer, "Reorder_Point" integer, "Image_URL" text, "Stock_Percent" numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  WITH "Product_Calc" AS (
  	SELECT
	    p."Product_ID",
	    p."Name" AS "Product_Name",
	    public."SP_ALL_CATEGORY_PATH_GET"(p."Child_ID") AS "Category_Name",
	    (p."Quantity" - p."Total_Sales" + p."Cancellation_Count") AS "Quantity",
	    p."Reorder_Point",
	    p."Image_URL",
		COALESCE(p."Is_Deleted", FALSE) AS "Is_Deleted",
		COALESCE(p."Visibility", TRUE) AS "Visibility"
	  FROM public."Product" p
  )
  SELECT
	pc."Product_ID",
    pc."Product_Name",
    pc."Category_Name",
    pc."Quantity",
    pc."Reorder_Point",
    pc."Image_URL",
	ROUND(
      CASE 
        WHEN pc."Reorder_Point" <= 0 THEN 0
        WHEN pc."Quantity" <= 0 THEN 0
        WHEN pc."Quantity" >= pc."Reorder_Point" THEN 100
        ELSE (pc."Quantity"::NUMERIC / pc."Reorder_Point") * 100
      END, 
      2
    ) AS "Stock_Percent"
  FROM "Product_Calc" pc
  WHERE pc."Is_Deleted" = FALSE
    AND pc."Visibility" = TRUE
    AND pc."Quantity" <= pc."Reorder_Point"
  ORDER BY "Stock_Percent" ASC
  LIMIT 5;
END;
$$;


