--
-- TOC entry 284 (class 1255 OID 16805)
-- Name: SP_ADMIN_DASHBOARD_SALES_GET(character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_ADMIN_DASHBOARD_SALES_GET"(p_mode character varying) RETURNS TABLE("Label" text, "TotalSales" numeric, "AvgSales" numeric, "DeviationPercent" numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF p_mode = 'daily' THEN
        RETURN QUERY
        WITH daily_data AS (
            SELECT
                d::date AS "Date",
                COALESCE(SUM(o."Total_Amount"), 0)::NUMERIC(12,2) AS "TotalSales"
            FROM generate_series(
                CURRENT_DATE - INTERVAL '29 day',
                CURRENT_DATE,
                INTERVAL '1 day'
            ) AS d
            LEFT JOIN public."Order" o
				ON DATE(o."Order_Date") = d::date
				AND (o."Status" = 'shipped' OR o."Status" = 'delivered')
			JOIN public."Order_Cancel" oc 
				ON oc."Order_ID" = o."Order_ID"
				AND COALESCE(oc."Is_Cancelled", FALSE) = FALSE
            GROUP BY d
        ),
        avg_cte AS (
            SELECT ROUND(AVG(dd."TotalSales"), 2) AS avg_val FROM daily_data dd
        )
        SELECT
            TO_CHAR(dd."Date", 'MM-DD') AS "Label",
            dd."TotalSales",
            avgc.avg_val AS "AvgSales",
            ROUND(
                CASE 
                    WHEN avgc.avg_val > 0 THEN 
                        ((dd."TotalSales" - avgc.avg_val) / avgc.avg_val) * 100
                    ELSE 0 
                END, 2
            ) AS "DeviationPercent"
        FROM daily_data dd, avg_cte avgc
        ORDER BY dd."Date";

    ELSIF p_mode = 'weekly' THEN
        RETURN QUERY
        WITH weekly_data AS (
            SELECT
                d::date AS "Date",
                COALESCE(SUM(o."Total_Amount"), 0)::NUMERIC(12,2) AS "TotalSales"
            FROM generate_series(
                CURRENT_DATE - INTERVAL '6 day',
                CURRENT_DATE,
                INTERVAL '1 day'
            ) AS d
			LEFT JOIN public."Order" o
				ON DATE(o."Order_Date") = d::date
				AND (o."Status" = 'shipped' OR o."Status" = 'delivered')
			JOIN public."Order_Cancel" oc 
				ON oc."Order_ID" = o."Order_ID"
				AND COALESCE(oc."Is_Cancelled", FALSE) = FALSE
            GROUP BY d
        ),
        avg_cte AS (
            SELECT ROUND(AVG(wd."TotalSales"), 2) AS avg_val FROM weekly_data wd
        )
        SELECT
            TO_CHAR(wd."Date", 'Dy') AS "Label",
            wd."TotalSales",
            avgc.avg_val AS "AvgSales",
            ROUND(
                CASE 
                    WHEN avgc.avg_val > 0 THEN 
                        ((wd."TotalSales" - avgc.avg_val) / avgc.avg_val) * 100
                    ELSE 0 
                END, 2
            ) AS "DeviationPercent"
        FROM weekly_data wd, avg_cte avgc
        ORDER BY wd."Date";

    ELSIF p_mode = 'monthly' THEN
        RETURN QUERY
        WITH monthly_data AS (
            SELECT
                date_trunc('month', o."Order_Date") AS "Month",
                COALESCE(SUM(o."Total_Amount"), 0)::NUMERIC(12,2) AS "TotalSales"
            FROM public."Order" o
			JOIN public."Order_Cancel" oc ON oc."Order_ID" = o."Order_ID"
            WHERE COALESCE(oc."Is_Cancelled", FALSE) = FALSE
              AND (o."Status" = 'shipped' OR o."Status" = 'delivered')
              AND date_part('year', o."Order_Date") = date_part('year', CURRENT_DATE)
            GROUP BY date_trunc('month', o."Order_Date")
        ),
        avg_cte AS (
            SELECT ROUND(AVG(md."TotalSales"), 2) AS avg_val FROM monthly_data md
        )
        SELECT
            TO_CHAR(md."Month", 'YYYY-MM') AS "Label",
            md."TotalSales",
            avgc.avg_val AS "AvgSales",
            ROUND(
                CASE 
                    WHEN avgc.avg_val > 0 THEN 
                        ((md."TotalSales" - avgc.avg_val) / avgc.avg_val) * 100
                    ELSE 0 
                END, 2
            ) AS "DeviationPercent"
        FROM monthly_data md, avg_cte avgc
        ORDER BY md."Month";

    ELSIF p_mode = 'quarterly' THEN
        RETURN QUERY
        WITH quarterly_data AS (
            SELECT
                EXTRACT(YEAR FROM o."Order_Date")::INT AS "Year",
                EXTRACT(QUARTER FROM o."Order_Date")::INT AS "Quarter",
                COALESCE(SUM(o."Total_Amount"), 0)::NUMERIC(12,2) AS "TotalSales"
            FROM public."Order" o
			JOIN public."Order_Cancel" oc ON oc."Order_ID" = o."Order_ID"
            WHERE COALESCE(oc."Is_Cancelled", FALSE) = FALSE
              AND (o."Status" = 'shipped' OR o."Status" = 'delivered')
            GROUP BY 1, 2
        ),
        avg_cte AS (
            SELECT ROUND(AVG(qd."TotalSales"), 2) AS avg_val FROM quarterly_data qd
        )
        SELECT
            CONCAT('Q', qd."Quarter", '-', qd."Year") AS "Label",
            qd."TotalSales",
            avgc.avg_val AS "AvgSales",
            ROUND(
                CASE 
                    WHEN avgc.avg_val > 0 THEN 
                        ((qd."TotalSales" - avgc.avg_val) / avgc.avg_val) * 100
                    ELSE 0 
                END, 2
            ) AS "DeviationPercent"
        FROM quarterly_data qd, avg_cte avgc
        ORDER BY qd."Year", qd."Quarter";

    ELSIF p_mode = 'yearly' THEN
        RETURN QUERY
        WITH yearly_data AS (
            SELECT
                EXTRACT(YEAR FROM o."Order_Date")::INT AS "Year",
                COALESCE(SUM(o."Total_Amount"), 0)::NUMERIC(12,2) AS "TotalSales"
            FROM public."Order" o
			JOIN public."Order_Cancel" oc ON oc."Order_ID" = o."Order_ID"
            WHERE COALESCE(oc."Is_Cancelled", FALSE) = FALSE
              AND (o."Status" = 'shipped' OR o."Status" = 'delivered')
            GROUP BY 1
        ),
        avg_cte AS (
            SELECT ROUND(AVG(yd."TotalSales"), 2) AS avg_val FROM yearly_data yd
        )
        SELECT
            yd."Year"::TEXT AS "Label",
            yd."TotalSales",
            avgc.avg_val AS "AvgSales",
            ROUND(
                CASE 
                    WHEN avgc.avg_val > 0 THEN 
                        ((yd."TotalSales" - avgc.avg_val) / avgc.avg_val) * 100
                    ELSE 0 
                END, 2
            ) AS "DeviationPercent"
        FROM yearly_data yd, avg_cte avgc
        ORDER BY yd."Year";

    ELSE
        RAISE EXCEPTION 'Invalid mode: %, expected daily|weekly|monthly|quarterly|yearly', p_mode;
    END IF;
END;
$$;


