CREATE FUNCTION public."SP_ADMIN_DASHBOARD_SUMMARY_GET"() RETURNS TABLE("Sales_Today" numeric, "Sales_Monthly" integer, "New_Orders" integer, "Total_Customers" integer)
    LANGUAGE plpgsql
    AS $$
BEGIN
    SELECT
        COALESCE(SUM("Total_Amount"), 0)
    INTO "Sales_Today"
    FROM public."Order" O
	JOIN public."Order_Cancel" OC ON OC."Order_ID" = O."Order_ID"
    WHERE DATE(O."Order_Date") = CURRENT_DATE
      AND COALESCE(OC."Is_Cancelled", FALSE) = FALSE
      AND (O."Status" = 'shipped' OR O."Status" = 'delivered');

	SELECT 
		COALESCE(SUM("Total_Amount"), 0)
	INTO "Sales_Monthly"
	FROM public."Order" O
	JOIN public."Order_Cancel" OC ON OC."Order_ID" = O."Order_ID"
	WHERE DATE_TRUNC('month', O."Order_Date") = DATE_TRUNC('month', CURRENT_DATE)
	AND COALESCE(OC."Is_Cancelled", FALSE) = FALSE
	AND (O."Status" = 'shipped' OR O."Status" = 'delivered');

    SELECT
        COUNT(*)
    INTO "New_Orders"
    FROM public."Order"
    WHERE DATE("Order_Date") = CURRENT_DATE;

    SELECT
        COUNT(*)
    INTO "Total_Customers"
    FROM public."User"
    WHERE "Is_Deleted" = FALSE
		AND "Access_Level" = '0';

    RETURN QUERY SELECT
        "Sales_Today",
        "Sales_Monthly",
        "New_Orders",
        "Total_Customers";
END;
$$;


