CREATE FUNCTION public."SP_ADMIN_DASHBOARD_TOP_PRODUCTS_GET"() RETURNS TABLE("Product_ID" integer, "Product_Name" character varying, "Product_Unit" character varying, "Total_Sales" bigint, "Quantity" integer, "Revenue" numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT
		p."Product_ID" AS "Product_ID",
        p."Name" AS "Product_Name",
		p."Unit" AS "Prodyct_Unit",
        SUM(d."Quantity") AS "Total_Sales",
		(p."Quantity" - p."Total_Sales" + p."Cancellation_Count") AS "Quantity",
        COALESCE(SUM(d."Quantity" * COALESCE(d."Product_Discount_Price", d."Product_Sale_Price")), 0)::NUMERIC(12,2) AS "Revenue"
    FROM public."Order_Detail" d
    JOIN public."Product" p ON p."Product_ID" = d."Product_ID"
    JOIN public."Order" h ON h."Order_ID" = d."Order_ID"
	JOIN public."Order_Cancel" oc ON oc."Order_ID" = d."Order_ID"
    WHERE COALESCE(p."Is_Deleted", FALSE) = FALSE
		AND COALESCE(oc."Is_Cancelled", FALSE) = FALSE
		AND COALESCE(p."Visibility", FALSE) = TRUE
		AND (h."Status" = 'shipped' OR h."Status" = 'delivered')
    GROUP BY p."Product_ID"
    ORDER BY "Total_Sales" DESC
    LIMIT 5;
END;
$$;


