-- Name: SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD(integer, text, boolean, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"("IN_Order_ID" integer, "IN_Action" text, "IN_Is_Req_Cancel" boolean, "IN_User_ID" integer) RETURNS TABLE("Is_Success" boolean, "Message" text)
    LANGUAGE plpgsql
    AS $$
DECLARE
	v_order_status TEXT;
	v_is_payment_checked BOOLEAN;
	v_transaction_status TEXT;
	v_success BOOLEAN;
	v_message TEXT;
BEGIN
	IF "IN_Action" = 'confirmed' THEN

		v_is_payment_checked := TRUE;
		v_transaction_status := 'confirmed';

		IF "IN_Is_Req_Cancel" THEN

			v_success := TRUE;
	        v_message := 'ปฏิเสธการชำระเงินสำเร็จ ระบบได้ดำเนินการเข้าสู่ขั้นตอนการคืนเงิน';
			v_order_status := 'refunding';

		ELSE
		
			v_success = TRUE;
	        v_message := 'ยืนยันการชำระเงินเรียบร้อยแล้ว';
			v_order_status := NULL;
			
		END IF;

	ELSIF "IN_Action" = 'rejected' THEN

		v_is_payment_checked := FALSE;
		v_transaction_status := 'rejected';

		IF "IN_Is_Req_Cancel" THEN

			v_success := TRUE;
	        v_message := 'ปฏิเสธการชำระเงินสำเร็จ และยกเลิกคำสั่งซื้อเรียบร้อยแล้ว';
			v_order_status := 'cancelled';
			
		ELSE

			v_success := TRUE;
	        v_message := 'ปฏิเสธการชำระเงิน';
			v_order_status := 'waiting_payment';
			
		END IF;

	ELSE
	
		v_success := FALSE;
	    v_message := 'Action ไม่ถูกต้อง';
		
	END IF;

	IF v_success THEN

		UPDATE public."Order_Payment"
		SET "Is_Payment_Checked" = v_is_payment_checked,
			"Status" = v_transaction_status,
			"Checked_By" = "IN_User_ID",
			"Checked_At" = NOW()
		WHERE "Order_ID" = "IN_Order_ID";

		IF v_order_status IS NOT NULL THEN
			
			PERFORM public."SP_UTIL_ORDER_STATUS_UPD"("IN_Order_ID", v_order_status, "IN_User_ID");

		END IF;
		
	END IF;

	RETURN QUERY SELECT v_success, v_message;
END
$$;


