-- TOC entry 281 (class 1255 OID 17092)
-- Name: SP_ADMIN_ORDER_REQ_CANCEL_STEP_GET(integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_ADMIN_ORDER_REQ_CANCEL_STEP_GET"("IN_Order_ID" integer) RETURNS TABLE("btnDisable" boolean, "lbButtonText" text, "isNoPaymentChecked" boolean, "isRefunding" boolean, "isCancelled" boolean, "Href" text)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_order RECORD;
	v_lock BOOLEAN;
	v_btnDisable BOOLEAN;
	v_isCOD BOOLEAN;
	v_isNoPaymentChecked BOOLEAN;
	v_isRefunding BOOLEAN;
	v_isCancelled BOOLEAN;
	v_lbButtonText TEXT;
	v_href TEXT DEFAULT NULL;
BEGIN
	SELECT * INTO v_order
    FROM public."SP_ADMIN_ORDER_GET"("IN_Order_ID");

	IF v_order IS NULL THEN
        RETURN QUERY 
		SELECT 
			FALSE, 
			'-', 
			FALSE,
			FALSE, 
			FALSE,
			NULL;
        RETURN;
    END IF;

	v_isCOD := v_order."Payment_Type" = 'cash_on_delivery';

	v_isNoPaymentChecked := v_isCOD = FALSE AND
		v_order."Transaction_Slip" IS NOT NULL AND
		v_order."Is_Payment_Checked" = FALSE AND
		v_order."Transaction_Status" = 'pending' AND
		v_order."Status" = 'pending' AND
		v_order."Is_Confirmed" = FALSE;

	IF v_order."Status" IN ('cancelled', 'refunding', 'refunded') THEN
        RETURN QUERY 
		SELECT
            FALSE, 
			'คำสั่งซื้อถูกยกเลิกแล้ว', 
			v_isNoPaymentChecked,
			FALSE, 
			FALSE,
			NULL;
        RETURN;
    END IF;

	v_lock := v_order."Status" = 'shipped' OR v_order."Status" = 'delivered';

	v_isRefunding := v_isCOD = FALSE AND
		v_order."Transaction_Slip" IS NOT NULL AND
		v_order."Is_Payment_Checked" = TRUE AND
		v_order."Transaction_Status" = 'confirmed';
		-- AND
		-- v_order."Status" = 'preparing' AND
		-- v_order."Is_Confirmed";

	IF v_isRefunding THEN
		v_href := '/admin/order-management/' || v_order."Order_ID" || '?controller=refunding';
	END IF;

	IF v_lock THEN
		v_isCancelled := FALSE;
	ELSE
		v_isCancelled := v_isRefunding = FALSE;
	END IF;

	IF v_isCancelled THEN
		v_href := '/admin/order-management';
	END IF;

	IF v_isNoPaymentChecked THEN
		v_btnDisable := FALSE;
		v_href := '/admin/order-management/' || v_order."Order_ID" || '?controller=checkorder&goto=transfer_slip';
	ELSE
		v_btnDisable := v_lock;
	END IF;

	IF v_btnDisable THEN
		v_lbButtonText := 'ไม่สามารถยกเลิกได้';
	ELSEIF v_isNoPaymentChecked THEN
		v_lbButtonText := 'ดำเนินการตรวจสอบหลักฐาน';
	ELSE
		v_lbButtonText := 'ยืนยัน';
	END IF;

    RETURN QUERY 
    SELECT 
		v_btnDisable,
		v_lbButtonText,
		v_isNoPaymentChecked,
		v_isRefunding,
		v_isCancelled,
		v_href;
END;
$$;


