--
-- TOC entry 268 (class 1255 OID 16813)
-- Name: SP_ADMIN_ORDER_REQ_CANCEL_UPD(integer, text, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_ADMIN_ORDER_REQ_CANCEL_UPD"("IN_Order_ID" integer, "IN_Reason" text, "IN_User_ID" integer) RETURNS TABLE("Is_Success" boolean, "Message" text, "Href" text)
    LANGUAGE plpgsql
    AS $$
DECLARE
	v_isNoPaymentChecked BOOLEAN;
	v_isRefunding BOOLEAN;
	v_isCancelled BOOLEAN;
	v_href TEXT;
BEGIN
	UPDATE public."Order_Cancel"
	SET "Is_Cancelled" = TRUE,
		"Cancel_Reason" = "IN_Reason",
		"Cancel_By" = "IN_User_ID",
		"Cancel_Date" = NOW()
	WHERE "Order_ID" = "IN_Order_ID";

	SELECT
		step."isNoPaymentChecked",
		step."isRefunding",
		step."isCancelled",
		step."Href"
	INTO
		v_isNoPaymentChecked,
		v_isRefunding,
		v_isCancelled,
		v_href
	FROM public."SP_ADMIN_ORDER_REQ_CANCEL_STEP_GET"("IN_Order_ID") step;

	IF v_isNoPaymentChecked THEN
		RETURN QUERY SELECT 
			FALSE, 
			'ไม่สามารถยกเลิกคำสั่งซื้อได้! เนื่องจากยังไม่ได้มีการตรวจสอบหลักฐานการชำระเงิน ต้องการไปยังหน้าดำเนินการตรวจสอบหลักฐานหรือไม่', 
			v_href;
		RETURN;
	END IF;

	IF v_isRefunding THEN
		PERFORM public."SP_UTIL_ORDER_STATUS_UPD"("IN_Order_ID", 'refunding', "IN_User_ID");
		RETURN QUERY SELECT 
			TRUE, 
			'ยกเลิกคำสั่งซื้อสำเร็จ! ต้องการไปยังหน้าดำเนินการคืนเงินตอนนี้หรือไม่?', 
			v_href;
		RETURN;
	END IF;

	IF v_isCancelled THEN
		PERFORM public."SP_UTIL_ORDER_STATUS_UPD"("IN_Order_ID", 'cancelled', "IN_User_ID");
		RETURN QUERY SELECT 
			TRUE, 
			'ยกเลิกคำสั่งซื้อสำเร็จ! ต้องการไปยังหน้าจัดการคำสั่งซื้อหรือไม่?', 
			v_href;
		RETURN;
	END IF;

	RETURN QUERY SELECT FALSE, 'ยกเลิกคำสั่งซื้อล้มเหลว', NULL;
END
$$;


