-- Name: SP_ALL_TOP_SELLING_GET(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_ALL_TOP_SELLING_GET"() RETURNS TABLE("Top_Categories" json, "Recommended_Products" json)
    LANGUAGE plpgsql
    AS $$
BEGIN
	RETURN QUERY
	SELECT 
		(SELECT json_agg(t)
		    FROM (
				SELECT
					ch."Name",
					CONCAT('https://placehold.co/300x200/D8BFD8/000000?text=', ch."Name") AS "Image",
					CONCAT('/products?categoryId=', c."Category_ID",
						   '&subCategoryId=', s."Sub_Category_ID",
						   '&childCategoryId=', ch."Child_ID") AS "Category_Path"
				FROM public."Category" c
				JOIN public."Sub_Category" s ON s."Category_ID" = c."Category_ID"
				JOIN public."Child_Sub_Category" ch ON ch."Sub_Category_ID" = s."Sub_Category_ID"
				JOIN public."Product" p ON p."Child_ID" = ch."Child_ID"
				WHERE COALESCE(p."Is_Deleted", FALSE) = FALSE
				GROUP BY c."Category_ID", s."Sub_Category_ID", ch."Child_ID", ch."Name"
				ORDER BY SUM(p."Total_Sales") DESC
				LIMIT 12
		    ) t
		) as "Top_Categories",
		(SELECT json_agg(p)
		    FROM (
				SELECT
					p.*
				FROM public."Order_Detail" d
				JOIN public."Product" p ON p."Product_ID" = d."Product_ID"
				JOIN public."Order" h ON h."Order_ID" = d."Order_ID"
				JOIN public."Order_Cancel" oc ON oc."Order_ID" = d."Order_ID"
				WHERE COALESCE(p."Is_Deleted", FALSE) = FALSE
					AND COALESCE(oc."Is_Cancelled", FALSE) = FALSE
					AND COALESCE(p."Visibility", FALSE) = TRUE
					AND (h."Status" = 'shipped' OR h."Status" = 'delivered')
				GROUP BY p."Product_ID"
				ORDER BY SUM(d."Quantity") DESC
				LIMIT 8
		    ) p
		) as "Recommended_Products";
END;
$$;


