CREATE FUNCTION public."SP_AUTH_LOGIN"("IN_Username" text, "IN_Password" text) RETURNS TABLE("Status_Code" integer, "Message" text, "User_ID" integer, "Username" character varying, "Full_Name" character varying, "Email" character varying, "Access_Level" integer)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_user RECORD;
BEGIN
	SELECT * INTO v_user
    FROM public."User" U
    WHERE U."Username" = "IN_Username" OR U."Email" = "IN_Username"
    LIMIT 1;

	IF NOT FOUND THEN
        "Status_Code" := 400;
		"Message" := 'ไม่สามารถเข้าสู่ระบบได้ ชื่อผู้ใช้หรืออีเมลไม่ถูกต้อง!';
		"User_ID" := NULL;
		"Username" := NULL;
		"Full_Name" := NULL;
		"Email" := NULL;
		"Access_Level" := 0;
		RETURN NEXT;
		RETURN;
    END IF;

	IF v_user."Is_Deleted" = TRUE THEN
		"Status_Code" := 403;
		"Message" := 'ไม่สามารถเข้าสู่ระบบได้ บัญชีถูกลบ!';
		"User_ID" := NULL;
		"Username" := NULL;
		"Full_Name" := NULL;
		"Email" := NULL;
		"Access_Level" := 0;
		RETURN NEXT;
        RETURN;
    END IF;

	IF v_user."Password" = "IN_Password" THEN
		"Status_Code" := 200;
		"Message" := 'เข้าสู่ระบบสำเร็จ!';
		"User_ID" := v_user."User_ID";
		"Username" := v_user."Username";
		"Full_Name" := v_user."Full_Name";
		"Email" := v_user."Email";
		"Access_Level" := v_user."Access_Level";
		RETURN NEXT;
		RETURN;
    END IF;

	"Status_Code" := 401;
	"Message" := 'ไม่สามารถเข้าสู่ระบบได้ รหัสผ่านไม่ถูกต้อง!';
	"User_ID" := NULL;
	"Username" := NULL;
	"Full_Name" := NULL;
	"Email" := NULL;
	"Access_Level" := NULL;
	RETURN NEXT;
	RETURN;
END;
$$;


