--

CREATE FUNCTION public."SP_MOCK_ORDER_USECASE_GET"("IN_Enable_Mock" boolean) RETURNS void
    LANGUAGE plpgsql
    AS $$

BEGIN
	DELETE FROM public."Order";
	DELETE FROM public."Order_Cancel";
	DELETE FROM public."Order_Detail";
	DELETE FROM public."Order_Payment";
	DELETE FROM public."Order_Receive";
	DELETE FROM public."Order_Refund";
	DELETE FROM public."Order_Shipping";
	DELETE FROM public."Order_Shipping_Address";

	PERFORM pg_catalog.setval('public."Order_Order_ID_seq"', 1000000, true);
		
	IF "IN_Enable_Mock" THEN
		-- 1. กรณีสลิปถูกปฏิเสธ (ต้องเห็นข้อความเตือนสีแดงให้แนบใหม่)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'slip_rejected');
		
		-- 2. กรณีสลิปตรวจสอบผ่านแล้ว (แต่ยังไม่ไปสถานะ Preparing)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'slip_verified');
		-- 1. สร้างออเดอร์รอจ่ายเงิน (Waiting Payment)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'waiting_payment');
		
		-- 2. สร้างออเดอร์แนบสลิปแล้ว รอตรวจ (Pending)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'pending');
		
		-- 3. สร้างออเดอร์ที่ตรวจสลิปและยืนยันแล้ว (Preparing)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'preparing');
		
		-- 4. สร้างออเดอร์เก็บเงินปลายทาง (COD - Preparing)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'cod');
		
		-- 5. สร้างออเดอร์ที่ส่งของแล้ว (Shipped)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'shipped');
		
		-- 6. สร้างออเดอร์ที่ขอยกเลิกหลังแนบสลิป (Req Cancel)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'req_cancel');
		
		-- 7. สร้างออเดอร์ที่ขอยกเลิกหลังยืนยันแล้ว รอคืนเงิน (Refunding)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'refunding');
		
		-- 8. สร้างออเดอร์ที่คืนเงินสำเร็จแล้ว (Refunded)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'refunded');
		
		-- 9. สร้างออเดอร์ที่ยกเลิกทันที (Cancelled)
		PERFORM "SP_MOCK_ORDER_USECASE_INS"(32, 'cancelled');
	END IF;
END
$$;


