--
-- TOC entry 298 (class 1255 OID 16848)
-- Name: SP_MOCK_ORDER_USECASE_INS(integer, text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_MOCK_ORDER_USECASE_INS"("IN_User_ID" integer, "IN_UseCase" text) RETURNS TABLE("Status_Code" integer, "Message" text, "Order_ID" integer, "Order_Status" text)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_admin_id INT := 1;
    v_address_id INT;
    v_product RECORD;
    v_cart_json JSONB;
    v_order_id INT;
    v_total_price NUMERIC;
    v_payment_type TEXT := 'bank_transfer';
BEGIN
    -- 1. เตรียมข้อมูล (ที่อยู่ และ สินค้า)
    SELECT "Address_ID" INTO v_address_id FROM public."Address" WHERE "User_ID" = "IN_User_ID" LIMIT 1;
    
    IF v_address_id IS NULL THEN
        VALUES ("IN_User_ID", '123/45 Mock Address', 'Sub-District', 'District', 'Province', '10000', '0812345678', TRUE)
        RETURNING "Address_ID" INTO v_address_id;
    END IF;

    SELECT * INTO v_product FROM public."Product" WHERE "Quantity" > 10 AND "Is_Deleted" = FALSE LIMIT 1;

    IF v_product IS NULL THEN
        RETURN QUERY SELECT 404, 'ไม่พบสินค้าที่มีสต็อกเพียงพอในระบบ', -1, NULL::text;
        RETURN;
    END IF;

    v_cart_json := jsonb_build_array(
        jsonb_build_object('Product_ID', v_product."Product_ID", 'Cart_Quantity', 1)
    );
    v_total_price := v_product."Sale_Price";

    IF "IN_UseCase" = 'cod' THEN
        v_payment_type := 'cash_on_delivery';
    END IF;

    -- 2. สร้างคำสั่งซื้อ
    SELECT "Out_Order_ID" INTO v_order_id 
    FROM public."SP_USER_ORDER_INS"("IN_User_ID", v_address_id, v_payment_type, v_total_price, v_cart_json);

    -- 3. จัดการสถานะตาม Use Case
    CASE "IN_UseCase"
        WHEN 'waiting_payment' THEN
            -- รอจ่ายเงิน (เริ่มต้น)
            NULL;

        WHEN 'pending' THEN
            -- แนบสลิปแล้ว (ยังไม่ตรวจ)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);

        WHEN 'slip_rejected' THEN
            -- [เพิ่มใหม่] แนบสลิป -> แอดมินปฏิเสธ (Status กลับเป็น waiting_payment, Transaction_Status = rejected)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'rejected', false, v_admin_id);

        WHEN 'slip_verified' THEN
            -- [เพิ่มใหม่] แนบสลิป -> แอดมินตรวจสอบผ่าน (Status = pending, Payment_Checked = TRUE)
            -- (สถานะนี้คือเงินเข้าแล้ว แต่แอดมินยังไม่ได้กด Confirm Order เพื่อไป Preparing)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'confirmed', false, v_admin_id);

        WHEN 'preparing' THEN
            -- ตรวจผ่าน -> ยืนยันออเดอร์ (Status = preparing)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'confirmed', false, v_admin_id);
            PERFORM public."SP_ADMIN_ORDER_CONFIRM_UPD"(v_order_id, v_admin_id);

        WHEN 'cod' THEN
            -- COD -> ยืนยันออเดอร์ (Status = preparing)
            PERFORM public."SP_ADMIN_ORDER_CONFIRM_UPD"(v_order_id, v_admin_id);

        WHEN 'shipped' THEN
            -- Preparing -> ใส่ Tracking -> Shipped
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'confirmed', false, v_admin_id);
            PERFORM public."SP_ADMIN_ORDER_CONFIRM_UPD"(v_order_id, v_admin_id);
            PERFORM public."SP_ADMIN_ORDER_SHIPPING_UPD"(v_order_id, '{
                "Shipping_Method": "third_party",
                "Shipping_Provider": "Kerry Mock",
                "Tracking_Number": "MOCK123456",
                "Shipping_Date": "2024-01-01 12:00:00"
            }'::jsonb, v_admin_id);
            PERFORM public."SP_UTIL_ORDER_STATUS_UPD"(v_order_id, 'shipped', v_admin_id);

        WHEN 'req_cancel' THEN
            -- แนบสลิป (ยังไม่ตรวจ) -> ขอยกเลิก
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_USER_ORDER_CANCEL_UPD"(v_order_id, "IN_User_ID", 'Test Request Cancel');

        WHEN 'refunding' THEN
            -- Preparing (ตรวจแล้ว) -> ขอยกเลิก (เป็น Refunding)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'confirmed', false, v_admin_id);
            PERFORM public."SP_ADMIN_ORDER_CONFIRM_UPD"(v_order_id, v_admin_id);
            PERFORM public."SP_USER_ORDER_CANCEL_UPD"(v_order_id, "IN_User_ID", 'Test Refund Request');

        WHEN 'refunded' THEN
            -- Refunding -> แนบสลิปคืนเงิน (Refunded)
            PERFORM public."SP_USER_ORDER_TRANS_SLIP_UPD"('/uploads/slips/mock_slip.jpg', v_order_id);
            PERFORM public."SP_ADMIN_ORDER_PAYMENT_VERIFY_UPD"(v_order_id, 'confirmed', false, v_admin_id);
            PERFORM public."SP_ADMIN_ORDER_CONFIRM_UPD"(v_order_id, v_admin_id);
            PERFORM public."SP_USER_ORDER_CANCEL_UPD"(v_order_id, "IN_User_ID", 'Test Refund Request');
            PERFORM public."SP_ADMIN_ORDER_REFUND_SLIP_UPD"('/uploads/refunds/mock_refund.jpg', v_order_id, v_admin_id);

        WHEN 'cancelled' THEN
            -- Waiting -> ยกเลิกทันที
            PERFORM public."SP_USER_ORDER_CANCEL_UPD"(v_order_id, "IN_User_ID", 'Test Cancel Immediate');

        ELSE
            RETURN QUERY SELECT 400, 'Invalid UseCase Type', -1, NULL::text;
            RETURN;
    END CASE;

    RETURN QUERY 
    SELECT 
        200 AS "Status_Code", 
        ('Success created usecase: ' || "IN_UseCase") AS "Message", 
        v_order_id AS "Order_ID",
        "IN_UseCase" AS "Order_Status";

EXCEPTION
    WHEN OTHERS THEN
        RETURN QUERY SELECT 500, SQLERRM, -1, NULL::text;
END;
$$;


ALTER FUNCTION public."SP_MOCK_ORDER_USECASE_INS"("IN_User_ID" integer, "IN_UseCase" text) OWNER TO postgres;

--
-- TOC entry 285 (class 1255 OID 16850)
-- Name: SP_SYSTEM_AUTO_CANCEL_EXPIRED_ORDERS_GET(integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_SYSTEM_AUTO_CANCEL_EXPIRED_ORDERS_GET"("IN_Hours_Limit" integer) RETURNS TABLE("Cancelled_Count" integer)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_order RECORD;
    v_count INT := 0;
BEGIN
    FOR v_order IN
        SELECT O."Order_ID", O."User_ID"
        FROM public."Order" O
        LEFT JOIN public."Order_Payment" OP ON O."Order_ID" = OP."Order_ID"
        WHERE 
          O."Status" = 'waiting_payment' AND O."Payment_Type" = 'bank_transfer'
          
          AND (
            CASE 
                WHEN OP."Transaction_Date" IS NOT NULL THEN 
                    OP."Transaction_Date" < (NOW() - ("IN_Hours_Limit" || ' hours')::INTERVAL)
                
                ELSE 
                    O."Order_Date" < (NOW() - ("IN_Hours_Limit" || ' hours')::INTERVAL)
            END
          )
    LOOP
        UPDATE public."Order_Cancel"
        SET 
            "Is_Cancelled" = TRUE,
            "Cancel_Reason" = 'หมดเวลาการชำระเงิน (System Auto Cancel)'
        WHERE "Order_ID" = v_order."Order_ID";

        PERFORM public."SP_UTIL_ORDER_STATUS_UPD"(v_order."Order_ID", 'cancelled', -1);
        
        v_count := v_count + 1;
    END LOOP;

    RETURN QUERY SELECT v_count;
END;
$$;


