-- Name: SP_USER_CART_INS(integer, integer, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_USER_CART_INS"("IN_User_ID" integer, "IN_Product_ID" integer, "IN_Quantity" integer) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_product RECORD;
    v_cart RECORD;
    v_new_qty INT;
    v_available_stock INT;
BEGIN
    SELECT "Name", "Quantity", "Total_Sales", "Cancellation_Count"
    INTO v_product
    FROM public."Product"
    WHERE "Product_ID" = "IN_Product_ID";

    IF v_product IS NULL THEN
        RAISE EXCEPTION 'Product not found.';
    END IF;

    v_available_stock := v_product."Quantity" - v_product."Total_Sales" + v_product."Cancellation_Count";

    SELECT "Quantity"
    INTO v_cart
    FROM public."Cart_Detail"
    WHERE "User_ID" = "IN_User_ID" AND "Product_ID" = "IN_Product_ID";

    IF FOUND THEN
        v_new_qty := v_cart."Quantity" + "IN_Quantity";
        
        IF v_new_qty > v_available_stock THEN
            RAISE EXCEPTION 'จำนวนสินค้าสำหรับ "%" จะเกินจำนวนสินค้าที่มีในสต็อก (% ชิ้น) (มีในตะกร้าแล้ว % ชิ้น)', 
                v_product."Name", v_available_stock, v_cart."Quantity";
        END IF;

        UPDATE public."Cart_Detail" 
        SET "Quantity" = v_new_qty
        WHERE "User_ID" = "IN_User_ID" AND "Product_ID" = "IN_Product_ID";
        
    ELSE
        IF "IN_Quantity" > v_available_stock THEN
            RAISE EXCEPTION 'จำนวนสินค้าที่ร้องขอ (% ชิ้น) เกินจำนวนสินค้าที่มีในสต็อก (% ชิ้น) สำหรับ "%"',
                "IN_Quantity", v_available_stock, v_product."Name";
        END IF;

            "User_ID", "Product_ID", "Quantity"
        )
        VALUES (
            "IN_User_ID", "IN_Product_ID", "IN_Quantity"
        );
    END IF;
END;
$$;


