--

CREATE FUNCTION public."SP_USER_ORDER_CANCEL_UPD"("IN_Order_ID" integer, "IN_User_ID" integer, "IN_Reason" text) RETURNS TABLE("Status_Code" integer, "Message" text, "New_Status" character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_order RECORD;
	v_isNoPaymentChecked BOOLEAN;
	v_isRefunding BOOLEAN;
	v_isCancelled BOOLEAN;
    v_new_status VARCHAR(50);
BEGIN
    SELECT * INTO v_order
    FROM public."SP_USER_ORDER_GET"('Order_ID', "IN_Order_ID");

    IF v_order."Order_ID" IS NULL OR v_order."User_ID" <> "IN_User_ID" THEN
        RETURN QUERY SELECT 404, 'ไม่พบคำสั่งซื้อ หรือ คุณไม่มีสิทธิ์ดำเนินการ', NULL::varchar;
        RETURN;
    END IF;

    IF v_order."Status" IN ('cancelled', 'refunding', 'refunded', 'req_cancel') THEN
         RETURN QUERY SELECT 400, 'คำสั่งซื้อนี้ได้รับการยกเลิกหรืออยู่ในกระบวนการยกเลิกแล้ว', v_order."Status"::varchar;
         RETURN;
    END IF;

	SELECT
		step."isNoPaymentChecked",
		step."isRefunding",
		step."isCancelled"
	INTO
		v_isNoPaymentChecked,
		v_isRefunding,
		v_isCancelled
	FROM public."SP_USER_ORDER_REQ_CANCEL_STEP_GET"("IN_Order_ID") step;

	IF v_isNoPaymentChecked THEN
		v_new_status := 'req_cancel';
	ELSEIF v_isRefunding THEN
		v_new_status := 'refunding';
	ELSEIF v_isCancelled THEN
		v_new_status := 'cancelled';
	END IF;

	IF v_new_status IS NULL THEN
		RETURN QUERY SELECT 400, 'ไม่สามารถร้องขอยกเลิกคำสั่งซื้อนี้ได้', v_order."Status"::varchar;
		RETURN;
	END IF;

	UPDATE public."Order_Cancel"
	SET "Is_Cancelled" = TRUE,
		"Cancel_Reason" = "IN_Reason"
	WHERE "Order_ID" = "IN_Order_ID";

    PERFORM public."SP_UTIL_ORDER_STATUS_UPD"("IN_Order_ID", v_new_status, NULL);

    RETURN QUERY SELECT 200, 'ดำเนินการยกเลิกสำเร็จ', v_new_status;

EXCEPTION
    WHEN OTHERS THEN
        RETURN QUERY SELECT 500, 'เกิดข้อผิดพลาดภายในเซิร์ฟเวอร์: ' || SQLERRM, NULL::varchar;
END;
$$;


