    LANGUAGE plpgsql
    AS $$
DECLARE
    v_order RECORD;
BEGIN
    SELECT * INTO v_order
    FROM public."SP_USER_ORDER_GET"('Order_ID', "IN_Order_ID");
	
    IF v_order IS NULL THEN
        RETURN QUERY SELECT 404, 'ไม่พบหมายเลขคำสั่งซื้อนี้ในระบบ';
        RETURN;
    END IF;

    IF v_order."User_ID" <> "IN_User_ID" THEN
        RETURN QUERY SELECT 403, 'คุณไม่มีสิทธิ์ดำเนินการกับคำสั่งซื้อนี้';
        RETURN;
    END IF;

	IF v_order."Status" = 'delivered' THEN
