        RETURN QUERY SELECT 400, 'ไม่สามารถยืนยันรับสินค้าได้ เนื่องจากสินค้ายังไม่ได้อยู่ในสถานะจัดส่ง';
        RETURN;
    END IF;

    UPDATE public."Order_Receive"
    SET 
        "Is_Received" = TRUE,
        "Received_At" = NOW()
    WHERE "Order_ID" = "IN_Order_ID";

	PERFORM public."SP_UTIL_ORDER_STATUS_UPD"("IN_Order_ID", 'delivered', NULL);

    RETURN QUERY SELECT 200, 'ยืนยันการรับสินค้าเรียบร้อยแล้ว';

EXCEPTION
    WHEN OTHERS THEN
        RETURN QUERY SELECT 500, 'เกิดข้อผิดพลาดภายในเซิร์ฟเวอร์: ' || SQLERRM;
