        INTO v_product
        FROM public."Product"
        WHERE "Product_ID" = v_item."Product_ID"
        FOR UPDATE;

        IF NOT FOUND THEN
            RAISE EXCEPTION 'ไม่พบรหัสสินค้า: %', v_item."Product_ID";
        END IF;

        v_available_stock := v_product."Quantity" - v_product."Total_Sales" + v_product."Cancellation_Count";
		
        IF v_item."Cart_Quantity" > v_available_stock THEN
            RAISE EXCEPTION 'สินค้า "%" มีในสต็อกไม่เพียงพอ', v_product."Name";
        END IF;

            "Order_ID", 
			"Product_ID", 
			"Quantity",
            "Product_Sale_Cost", 
			"Product_Sale_Price", 
			"Product_Name",
            "Product_Brand", 
			"Product_Unit", 
			"Product_Image_URL", 
			"Product_Discount_Price"
        ) VALUES (
            v_order_id, 
			v_item."Product_ID", 
			v_item."Cart_Quantity",
            v_product."Sale_Cost", 
			v_product."Sale_Price", 
			v_product."Name",
            v_product."Brand", 
			v_product."Unit", 
			v_product."Image_URL", 
			v_product."Discount_Price"
        );

        UPDATE public."Product"
        SET "Total_Sales" = "Total_Sales" + v_item."Cart_Quantity"
        WHERE "Product_ID" = v_item."Product_ID";
    END LOOP;

	DELETE FROM public."Cart_Detail"
    WHERE "User_ID" = "IN_User_ID";

	RETURN QUERY SELECT 201, 'สร้างคำสั่งซื้อสำเร็จ', v_order_id;

EXCEPTION
    WHEN OTHERS THEN
        RAISE NOTICE 'เกิดข้อผิดพลาด: %', SQLERRM;
        RETURN QUERY SELECT 500, SQLERRM, -1;
END;
$$;


