--

CREATE FUNCTION public."SP_USER_PRODUCT_DETAIL_GET"("IN_Product_ID" integer) RETURNS TABLE("Status_Code" integer, "Message" text, "Product" jsonb, "Related_Products" jsonb, "Category_Path" jsonb)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_product RECORD;
BEGIN
	SELECT 
		P."Product_ID",
		P."Child_ID",
		P."Name",
		P."Brand",
		P."Description",
		P."Unit",
		P."Quantity",
		P."Sale_Cost",
		P."Sale_Price",
		P."Visibility",
		P."Image_URL",
		P."Dimensions",
		P."Material",
		P."Discount_Price",
		P."Total_Sales",
		P."Cancellation_Count"
    INTO v_product
    FROM public."Product" P
    WHERE P."Product_ID" = "IN_Product_ID" AND P."Visibility" = TRUE
    LIMIT 1;

    IF NOT FOUND THEN
        RETURN QUERY
        SELECT 
			404 AS "Status_Code",
            'ไม่พบสินค้า' AS "Message",
            NULL::jsonb AS "Product",
            NULL::jsonb AS "Related_Products",
            NULL::jsonb AS "Category_Path";
        RETURN;
    END IF;

	IF v_product."Child_ID" IS NOT NULL THEN
    	RETURN QUERY
		SELECT
			200 AS "Status_Code",
            'Success' AS "Message",
            to_jsonb(v_product) AS "Product",
            (
                SELECT jsonb_agg(rp)
                FROM (
                    SELECT * 
                    FROM public."Product" 
                    WHERE "Child_ID" = v_product."Child_ID"
                      AND "Product_ID" != v_product."Product_ID"
                      AND "Visibility" = TRUE
                    LIMIT 4
                ) rp
            ) AS "Related_Products",
            (
                SELECT to_jsonb(cp)
                FROM (
                    SELECT * FROM public."SP_ALL_CATEGORY_GET"(v_product."Child_ID")
                ) cp
            ) AS "Category_Path";
    ELSE
        RETURN QUERY
        SELECT
			200 AS "Status_Code",
            'Success' AS "Message",
            to_jsonb(v_product) AS "Product",
            NULL::jsonb AS "Related_Products",
            NULL::jsonb AS "Category_Path";
    END IF;
END;
$$;


