-- Name: SP_USER_PRODUCT_SEARCH_GET(text, integer, integer, integer, boolean, integer, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_USER_PRODUCT_SEARCH_GET"("IN_Search" text DEFAULT NULL::text, "IN_Category_ID" integer DEFAULT NULL::integer, "IN_Sub_Category_ID" integer DEFAULT NULL::integer, "IN_Child_ID" integer DEFAULT NULL::integer, "IN_Is_Discount" boolean DEFAULT false, "IN_Page" integer DEFAULT 1, "IN_Page_Size" integer DEFAULT 20) RETURNS TABLE("Product_ID" integer, "Name" character varying, "Brand" character varying, "Unit" character varying, "Sale_Cost" numeric, "Sale_Price" numeric, "Discount_Price" numeric, "Image_URL" text, "Quantity" integer, "Total_Sales" integer, "Cancellation_Count" integer, "Child_ID" integer, "Total_Rows" bigint)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_offset INT;
BEGIN
    -- คำนวณจุดเริ่มต้น (Offset)
    v_offset := ("IN_Page" - 1) * "IN_Page_Size";

    RETURN QUERY
    SELECT 
        p."Product_ID",
        p."Name",
        p."Brand",
        p."Unit",
        p."Sale_Cost",
        p."Sale_Price",
        p."Discount_Price",
        p."Image_URL",
        p."Quantity",
        p."Total_Sales",
        p."Cancellation_Count",
        p."Child_ID",
        COUNT(*) OVER() AS "Total_Rows" -- นับจำนวนรวมทั้งหมดของผลลัพธ์
    FROM public."Product" p
    LEFT JOIN public."Child_Sub_Category" csc ON p."Child_ID" = csc."Child_ID"
    WHERE 
        p."Is_Deleted" = FALSE 
        AND p."Visibility" = TRUE
        -- เงื่อนไขการค้นหา (Search)
        AND (
            "IN_Search" IS NULL OR 
            p."Name" ILIKE '%' || "IN_Search" || '%' OR 
            p."Brand" ILIKE '%' || "IN_Search" || '%'
        )
        -- เงื่อนไขหมวดหมู่ (Category)
        AND ("IN_Child_ID" IS NULL OR p."Child_ID" = "IN_Child_ID")
        AND ("IN_Sub_Category_ID" IS NULL OR csc."Sub_Category_ID" = "IN_Sub_Category_ID")
        AND ("IN_Category_ID" IS NULL OR csc."Category_ID" = "IN_Category_ID")
        -- เงื่อนไขสินค้าลดราคา
        AND ("IN_Is_Discount" = FALSE OR (p."Discount_Price" IS NOT NULL AND p."Discount_Price" < p."Sale_Price"))
    ORDER BY p."Product_ID" ASC
    LIMIT "IN_Page_Size"
    OFFSET v_offset;
END;
$$;


