-- Name: SP_UTIL_DYNAMIC_UPDATE(text, text, integer, jsonb); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public."SP_UTIL_DYNAMIC_UPDATE"("IN_TableName" text, "IN_Column" text, "IN_Value" integer, "IN_Data" jsonb) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_sql TEXT;
    v_set TEXT := '';
    r RECORD;
BEGIN
    FOR r IN SELECT * FROM jsonb_each_text("IN_Data")
    LOOP
        IF v_set <> '' THEN
            v_set := v_set || ', ';
        END IF;
        v_set := v_set || format('"%s" = %L', r.key, r.value);
    END LOOP;

    IF v_set = '' THEN
        RETURN;
    END IF;
	
    v_sql := format('UPDATE %I SET %s WHERE "%s" = %L RETURNING *', 
	"IN_TableName", 
	v_set, 
	"IN_Column", 
	"IN_Value");

    EXECUTE v_sql;
END;
$$;


